import React, { useEffect, useState } from "react";
import styled from "styled-components";
import axios from "axios";
import { Marginer } from "../../components/marginer";
import { PictureCard } from "../../components/pictureCard";
import PictureOne from "../../images/Artwork/PictureOne.jpg";
import PictureThree from "../../images/Artwork/PictureThree.jpg";
import PictureFive from "../../images/Artwork/PictureFive.jpg";
import PictureSeven from "../../images/Artwork/PictureSeven.jpg";


const InnerContainer = styled.div`

    width: 100%;
    height: 100%;
    display: flex;
    align-items: center;
    padding: 0em
    justify-content: space-between; 
    flex-direction: row;

    @media screen and (max-width: ${1220}px) {
        display: block;        
  }
`;
const PictureContainer = styled.div`
    display:block;
    width: 80%;
    box-shadow: 0px 0px 15px rgb(0,0,0);
    outline-style: ridge;
    outline-width: 13px;
    outline-color: #d8b1b1;


`;


const PictureThumbnail = styled.div`
    width: 100%;
    height: 30em;


    img{
        width: 100%;
        height: 100%;
    }
`;

const FavouriteContainer = styled.div`
    width: 100%;
    display: flex;
    background-color:#f8dfdf;
    flex-direction:row;
    align-items: center;
    justify-content: space-evenly;
`;

const FavWrapper = styled.div`
    display: flex;
    flex-wrap:wrap;
    flex-direction: row;
    @media screen and (max-width: ${980}px) {
        display: block;
       flex-direction: column;
       justify-content: center;
    }
`;


const WarningText = styled.h3`
    color: rgb(0, 0, 0);
    font-weight: 500;

`;

export function Artwork(props) {

    return (
        <FavouriteContainer>

            <FavWrapper>
                <InnerContainer>
                    <PictureContainer>
                        <PictureThumbnail>
                            <img src={PictureOne} alt="Thamina Art Logo" />
                        </PictureThumbnail>
                    </PictureContainer>
                    <Marginer direction="horizontal" margin={150} />

                    <PictureContainer>
                        <PictureThumbnail>
                            <img src={PictureThree} alt="Thamina Art Logo" />
                        </PictureThumbnail>
                    </PictureContainer>
                    <Marginer direction="horizontal" margin={150} />
                    <PictureContainer>
                        <PictureThumbnail>
                            <img src={PictureFive} alt="Thamina Art Logo" />
                        </PictureThumbnail>
                    </PictureContainer>
                    <Marginer direction="horizontal" margin={150} />
                    <PictureContainer>
                        <PictureThumbnail>
                            <img src={PictureSeven} alt="Thamina Art Logo" />
                        </PictureThumbnail>
                    </PictureContainer>
                </InnerContainer>
            </FavWrapper>
        </FavouriteContainer>


    )


    /* </FavouriteContainer>   const [offeredPics, setPics] = useState([]);
