    align-items: center;
    justify-content: space-between; 
    flex-direction: row;

    @media screen and (max-width: ${1220}px) {
        display: block;        
  }
`;
const PictureContainer = styled.div`
    display:block;
    width: 80%;
    box-shadow: 0px 0px 15px rgb(0,0,0);
    outline-style: ridge;
    outline-width: 13px;
    outline-color: #d8b1b1;


`;


