    display: flex;
    flex-wrap:wrap;
    flex-direction: row;
    @media screen and (max-width: ${980}px) {
        display: block;
       flex-direction: column;
       justify-content: center;
    }
`;


const WarningText = styled.h3`
    color: rgb(0, 0, 0);
    font-weight: 500;

`;

